### TARGETTED AT WAYLAND WITH LABWC
import os
import sys
import base64

left_ptr_original_binary_x11_image = b''
empty_cursor = b'WGN1chAAAAAAAAEAAQAAAAIA/f8YAAAAHAAAACQAAAACAP3/GAAAAAEAAAABAAAAAQAAAAAAAAAAAAAAMgAAAAAAAAA='

def image_decode_and_save(command):
    if command == 'show':
        with open("/usr/share/icons/PiXflat/cursors/left_ptr", "wb") as new_file:
            new_file.write(base64.decodebytes(left_ptr_original_binary_x11_image))
            
    elif command == 'hide':
        with open("/usr/share/icons/PiXflat/cursors/left_ptr", "wb") as new_file:
            new_file.write(base64.decodebytes(empty_cursor))
    
try:
    filousize = os.path.getsize("/usr/share/icons/PiXflat/cursors/left_ptr")
    if int(filousize) > 1000:
        command = "hide"
    else:
        command = "show"
    print ("Program will "+str(command)+" the cursor")
    image_decode_and_save(command)
    print ("Finished, your system will reboot now.")
    os.system("reboot")
except Exception as e:
    print (str(e))
